mapper.select:
select ID, IF_ID, IF_NAME, IF_SYS_ID, IF_PRO_ID, RSP_CODE_ID, RSP_CODE, CASE_NAME, CHECK_JSON, RSP_REF_JSON, RSP_CODE_DESC from (select au.ID, au.IF_ID, au.CASE_NAME, au.CHECK_JSON, au.RSP_REF_JSON, (select IF_NAME from T_IF_INTERFACE ii where ii.ID=au.IF_ID ) IF_NAME, (select IF_SYS_ID from T_IF_INTERFACE ii where ii.ID=au.IF_ID ) IF_SYS_ID, (select IF_PRO_ID from T_IF_INTERFACE ii where ii.ID=au.IF_ID ) IF_PRO_ID, au.RSP_CODE_ID, (select rc.RSP_CODE from T_IF_RETRUN_CODE rc where rc.ID=au.RSP_CODE_ID) RSP_CODE, (select rc.RSP_CODE_DESC from T_IF_RETRUN_CODE rc where rc.ID=au.RSP_CODE_ID) RSP_CODE_DESC
from T_IF_AUTO_MOCK au where STATUS='Y' ) tt where tt.IF_NAME like '%${ifName}%'
and tt.IF_SYS_ID = #{ifSysId}
and tt.IF_PRO_ID = #{ifProId}

mapper.update:

update T_IF_AUTO_MOCK
set REAL_RSP_CODE=#{realRspCode},
REAL_RESPONSE_JSON=#{realResponseJson},
TEST_RESULT=#{testResult}
where id=#{id}
